        //     "country":"United States",
        //     "image":"https://i.ibb.co/0ZgcGL0/new-york.jpg"
        //  }
    ]
}
const rootReducer = (state = initState, action)=>{
    if(action.type == "GET_CITIES"){
        state = {
            ...state, 
            cities: action.cities
        }
    }

    return state;
}

export default rootReducer;
