import React, { Component } from 'react';
import '../Styles/SignUpForm.css'
class SignUpForm extends Component {
    constructor(props){
        super(props);
        this.state ={
            username: "",
            password: "",
            email: "",
            firstName: "",
            lastName: "",
            country:[
                "England",
                "Germany",
                "France",
                "Holland",
                "Ireland",
                "Spain",
                "United States"
            ],
            agree: true,
            value: "",
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChange(e){

        // this.setState({[e.target.name]:e.target.value});
        if (e.target.id == 'username' )
        this.setState({username:e.target.value});
        if (e.target.id == 'password')
        this.setState({password:e.target.value});
        console.log(this.state)
    }
    onSubmit(e){
        e.preventDefault();
        console.log(this.state);

    }
    render(){
        console.log(this.state)
        const options = this.state.country.map((option,i)=>{
            return(
                <option value={option} key={option}>{option}</option>
            )
        })

        return(
            <form onSubmit={this.onSubmit} className="signUp" encType="multipart/form-data">
                <h3>Create Account</h3>
                <div className="addPhoto">
                    <label>Add Photo</label>
                    <input type="file"
                    name="avatar"
                    accept=".png, .jpg, .jpeg"/>                   
                </div>
                <div className="formParts">
                    <label>Username:</label>
                    <input
                    id='username'
                    type="text"
                    name="username"
                    value ={this.state.username}
                    onChange={(e) => this.onChange(e)}/>
                </div>
                {console.log(this.state.username)}
                <div className="formParts">              
                    <label>Password:</label>
                    <input
                    id='password'
                    type="text"
                    name="password"
                    value ={this.state.password}
                    onChange ={(e)=>this.onChange(e)}/>
                </div>
                <div className="formParts">
                    <label>Email:</label>
                    <input
                    type="text"
                    name="email"
                    value ={this.state.email}
                    onChange = {this.onChange}/>
                </div>
                <div className="formParts">
                    <label>First Name:</label>
                    <input
                    type="text"
                    name="firstName"
                    value ={this.state.firstName}
                    onChange = {this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Last Name:</label>
                    <input
                    type="text"
                    name="lastName"
                    value ={this.state.lastName}
                    onChange ={this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Country:</label>
                    <select
                    name="country"
                    value ={this.state.value}
                    onChange ={this.onChange} multiple={false} >
                        <option value="" disabled>Choose your country</option>
                        {options}
                    </select>
                </div>
                <div className="formParts">
                   <input id ="checkbox_id" type="checkbox" name="agree" checked={this.state.agree}
                   onChange ={this.onChange}/><label htmlFor="checkbox_id" className="TandC">I agree to MYtinerary's Terms and Conditions</label>
                </div>
                <button type="submit" className="signUpBtn">OK</button>
            </form>
        );
    }

}

export default SignUpForm;
