import React, { Component } from 'react';
import '../Styles/SignUpForm.css'
class SignUpForm extends Component {
    constructor(props){
        super(props);
        this.state ={
            username: "",
            password: "",
            email: "",
            firstName: "",
            lastName: "",
            country:[
                "England",
                "Germany",
                "France",
                "Holland",
                "Ireland",
                "Spain",
                "United States"
            ],
            countries:{
                "England":"England",
                "Germany":"Germany",
                "France":"France",
                "Holland":"Holland",
                "Ireland":"Ireland",
                "Spain":"Spain",
                "United States": "United States"
            },
            value: "",
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChange(e){

        // this.setState({[e.target.name]:e.target.value});
        this.setState({value:e.target.value});
    }
    onSubmit(e){
        e.preventDefault();
        console.log(this.state);

    }
    render(){
        console.log(this.state)
        const options = this.state.country.map((option,i)=>{
            return(
                <option value={option} key={option}>{option}</option>
            )
        })
        console.log(options)
        return(
            <form onSubmit={this.onSubmit} className="signUp">
                <h3>Create Account</h3>
                <div className="addPhoto">
                    <label>Add Photo</label>
                    <input type="file"
                    name="avatar"/>

                </div>
                <div className="formParts">
                    <label>Username:</label>
                    <input
                    type="text"
                    name="username"
                    value ={this.state.username}
                    onChange={this.onChange}/>
                </div>
                {console.log(this.state.username)}
                <div className="formParts">              
                    <label>Password:</label>
                    <input
                    type="text"
                    name="password"
                    value ={this.state.password}
                    onChange ={this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Email:</label>
                    <input
                    type="text"
                    name="email"
                    value ={this.state.email}
                    onChange = {this.onChange}/>
                </div>
                <div className="formParts">
                    <label>First Name:</label>
                    <input
                    type="text"
                    name="firstName"
                    value ={this.state.firstName}
                    onChange = {this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Last Name:</label>
                    <input
                    type="text"
                    name="lastName"
                    value ={this.state.lastName}
                    onChange ={this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Country:</label>
                    <select
                    name="country"
                    value ={this.state.value}
                    onChange ={this.onChange} multiple={false} >
                        <option value="" disabled>Choose your country</option>
                        {options}
                    </select>
                </div>
                <div className="formParts">
                   <input id ="checkbox_id" type="checkbox" name="yo" value="yo"/><label htmlFor="checkbox_id" className="TandC">I agree to MYtinerary's Terms and Conditions</label>
                </div>
                <button type="submit" className="signUpBtn">OK</button>
            </form>
        );
    }

}

export default SignUpForm;
