                //    "country":"Spain",
                //    "image":"https://i.ibb.co/QCMNzsK/barcelona.jpg"
                // },
                // {  
                //    "name":"Berlin",
                //    "country":"Germany",
                //    "image":"https://i.ibb.co/9hGRxT4/berlin.jpg"
                // },
                // {  
                //    "name":"London",
                //    "country":"United Kingdom",
                //    "image":"https://i.ibb.co/Kw2VPwV/london.jpg"
                // },
                // {  
                //    "name":"Paris",
                //    "country":"France",
                //    "image":"https://i.ibb.co/J52FX4z/paris.jpg"
                // },
                // {  
                //    "name":"Rome",
                //    "country":"Italy",
                //    "image":"https://i.ibb.co/s5KLmds/rome.jpg"
                // }
                "Amsterdam",
                "Barcelona",
                "pen",
                "mike",
                "dog",
                "cat",
                "iphone",
                "book"

             ]

           ,
            filtered:[]
        }
    }
    componentDidMount(){
        this.setState({filtered: this.state.all})
    }
    filterList=(e)=>{
        const updateList = this.state.all.filter((item)=>{
            return item.toLowerCase().search(e.target.value.toLowerCase()) !== -1;
        })
        this.setState({filtered: updateList})
    }
    render(){
        return(
            <div>
                <form>
                     <input type="text" placeholder="Search your destination!" onChange={this.filterList}/>

                </form>
                <div>
                    {this.state.filtered.map((item, index)=>{
                        return(
                            <li key={index}>{item}</li>
                        )
                    })}
                </div>

            </div>
        );
    }
}

export default Filter;
