import React, { Component } from 'react';
import CityData from './json.json';
// import RandomShow from './RandomShow.js';
// import { Slide } from 'react-slideshow-image';

class PostList extends Component {
  constructor(props){
    super(props);
    this.RandomShow = this.RandomShow.bind(this);
  }
  RandomShow(){
    let a = CityData.length;
    let b = [];
    for(let i=0; i< a; i++){
      b.push[Math.floor(Math.random()*a)];
    }
    return b;
  }
  render() {
    // const settings = {
    //   duration: 5000,
    //   transitionDuration: 500,
    //   infinite: true,
    //   indicators: true,
    //   arrows: true
    // };


    return (
      // <div>
      //   <Slide {...settings}>


      //   </Slide>
      // </div>
      <div className="Popular">
      {this.RandomShow.slice(0,4).map((postDetail, index)=>{
          return( <div>{postDetail.name}<img src={postDetail.image} alt="destinations"/></div>)
      })}

      </div>
    );


  }
}

export default PostList;
