import React, { Component } from 'react';
import '../Styles/SignUpForm.css'

const countries=["England",
"Germany",
"France",
"Holland",
"Ireland",
"Spain",
"United States"];

class SignUpForm extends Component {
    constructor(props){
        super(props);
        this.state ={
            username: "",
            password: "",
            email: "",
            firstName: "",
            lastName: "",
            country:"",
            hasAgreed: "",
            // value: "",
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChange(e){

        this.setState({[e.target.name]:e.target.value});

        // this.setState({value:e.target.value});

        // console.log(this.state)
    }
    onSubmit(e){
        e.preventDefault();
        console.log(this.state);
        if (this.state.username == "") {
           console.log("Username Cannot be empty!");
          }

    }

    render(){

        const options = countries.map((option,i)=>{
            return(
                <option value={option} key={option}>{option}</option>
            )
        })

        return(
            <form onSubmit={this.onSubmit} className="signUp" encType="multipart/form-data">
                <h3>Create Account</h3>
                <div className="addPhoto">
                    <label>Add Photo</label>
                    <input type="file"
                    name="avatar"
                    accept=".png, .jpg, .jpeg"/>                   
                </div>
                <div className="formParts">
                    <label>Username:</label>
                    <input
                    id='username'
                    type="text"
                    name="username"
                    value ={this.state.username}
                    onChange={this.onChange}/>
                </div>

                <div className="formParts">              
                    <label>Password:</label>
                    <input
                    id='password'
                    type="text"
                    name="password"
                    value ={this.state.password}
                    onChange ={this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Email:</label>
                    <input
                    id='email'
                    type="text"
                    name="email"
                    value ={this.state.email}
                    onChange = {this.onChange}/>
                </div>
                <div className="formParts">
                    <label>First Name:</label>
                    <input
                    id="firstName"
                    type="text"
                    name="firstName"
                    value ={this.state.firstName}
                    onChange = {this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Last Name:</label>
                    <input
                    id="lastName"
                    type="text"
                    name="lastName"
                    value ={this.state.lastName}
                    onChange ={this.onChange}/>
                </div>
                <div className="formParts">
                    <label>Country:</label>
                    <select
                    id="value"
                    name="country"
                    value ={this.state.value}
                    onChange ={this.onChange} multiple={false} >
                        <option value="" disabled>Choose your country</option>
                        {options}

                    </select>
                </div>
                <div className="formParts">
                   <input id ="checkbox_id" type="checkbox" name="hasAgreed"
                   onChange ={this.onChange}
                   value={this.state.hasAgreed}/>
                   <label htmlFor="checkbox_id" className="TandC">I agree to MYtinerary's &nbsp;<a href="">Terms and Conditions</a></label>
                </div>
                <button type="submit" className="signUpBtn">OK</button>
            </form>
        );
    }

}

export default SignUpForm;



