import React, { Component } from 'react';
import Hamburger from './Nav';
import UserIcon from './UserIcon';
import { connect } from 'react-redux';
import * as actionCreator from './Store/Actions/actions';
import ShowItinerary from './ShowItinerary';
import './Styles/Mytinerary.css';

let cityUrl = window.location.pathname.split("/")[2];
let menu = document.getElementById('all');
function show(){
    let hideContent = this.nextElementSibling;
    hideContent.classList.toggle('hide');
};
for(let i = 0; i < menu.length; i++){
    menu[i].addEventListener('click', show)
  }

class MYtinerary extends Component {
    constructor(){
        super()
        this.state ={
            itineraries:{},          

        };
    }
    componentDidMount(){
        this.props.fetchItineraries(cityUrl);
    }
    isObjectEmpty = (obj)=>{
        if(Object.keys(obj).length === 0){
            return true
        }else{
            return false
        }
    }    


    render() {
        const styles = {
            border: '1px solid black',
            height:'20vh',
            display:'flex',
            flexDirection: 'column'
        }
        const style2 ={
            display:'flex',
            justfyContent: 'space-between'
        }
        const style3 ={
            background: 'gray',
            height: '10vh',
            textAlign: 'center'
        }
        const style4 = {
            width: '30%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center'
        }
        const style5 = {
            width: '70%'
        }
        const style6 ={
            width: '6em',
            height: '6em',
            borderRadius: '50%'
        }
        const style7 ={
            display: 'flex',
            justifyContent: 'space-around',

        }
        const margin = {
            margin: '0'
        }
        if(this.isObjectEmpty(this.props.itineraries)=== false ){
            const itineraries = this.props.itineraries.itineraries;



        return (
            <div>
                <Hamburger /> 
                <UserIcon />            
                <p>Available MYtineraries</p>
                {this.props.itineraries.cities.map((obj,index)=>{
                    return (
                    <div>

                         <img src={obj.image} className="cover"/>
                         <div className="coverName">{obj.cityName}</div>
                    </div>
                    )
                })}

               {itineraries.map((obj, index)=>{
                   return (
                   <div>
                       {/* <div>{obj.city}</div> */}
                        <div style={styles}>
                            <div style={style2}>
                                <div style={style4}><img src={obj.profilePic}alt="user" style ={style6}></img><p style={margin}>{obj.user}</p></div>
                                <div style={style5}>
                                    <div><h5>{obj.title}</h5></div> 
                                    <div style={style7}>
                                        <div>Likes:{obj.rating}</div>
                                        <div>{obj.duration}Hours</div>
                                        <div>{obj.price}</div>
                                    </div>
                                <div>{"#"+obj.hashtag.join("#")}</div>
                                </div>
                            </div>
                            {/* <div style={style3}>View All</div>
                            <div>


                               <ShowItinerary images={obj.photos}/>
                            </div> */}
                            <details style={style3}>
                                <summary>View All</summary>
                                <ShowItinerary images={obj.photos}/>
                            </details>
                            {/* <div className="accordion-wrapper">
                                <p id="all">View All</p>
                                <div className="hide">
                                <ShowItinerary images={obj.photos}/>
                                </div>

                            </div> */}
                        </div>

                   </div>
                   )
                })}
               <div><a href="/Cities">Choose another city...</a></div>
            </div>

        );
    }else{
        return(
            <div>Loading...</div>
        )
    }
    }

}

const mapStateToProps =(state)=>{
    return {
        itineraries: state.itineraries,

    }
}
const mapDispatchToProps =(dispatch)=>{
    return {
        fetchItineraries: (city) => {
            dispatch(actionCreator.fetchItineraries(city))
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MYtinerary);
