import React, { Component } from 'react';
import '../Styles/SignUpForm.css'

const initialState = {
    username: "",
    password: "",
    email: "",
    firstName: "",
    lastName: "",
    country:"",
    hasAgreed: "",
    usernameError: "",
    passwordError: "",
    emailError: "",
    firstNameError: "",
    lastNameError: "",
    countryError:"",
    hasAgreedError: "",

  };
const countries=["England",
"Germany",
"France",
"Holland",
"Ireland",
"Spain",
"United States"];

class ValidationForm extends Component {

    state = initialState;

    // this.onChange = this.onChange.bind(this);
    //     this.onSubmit = this.onSubmit.bind(this);

    onChange(e){
        const target = e.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        this.setState({
        [name]: value
        });
    }





    validate= () =>{
        let usernameError = "";
        let emailError = "";
        // let passwordError = "";

        if (!this.state.username) {
          usernameError = "username cannot be blank";
        }

        if (!this.state.email.includes("@")) {
          emailError = "invalid email";
        }

        if (emailError || usernameError) {
          this.setState({ emailError, usernameError });
          return false;
        }

        return true;
      };

    onSubmit(e){
        e.preventDefault();
        console.log(this.state);
        const isValid = this.validate();

        if (isValid) {
        console.log(this.state);
        // clear form
        this.setState(initialState);
        }
    }
    render(){
        console.log(this.state)
        const options = countries.map((option, key)=>{
            return(
            <option value={option} key={option}>{option}</option>
            )
        })

        return(
            <div>
                <form onSubmit={this.onSubmit}>
                    <div>
                        <label htmlFor="username">Username</label>
                        <input type="text" 
                        onChange={this.onChange}
                        value={this.state.username}
                        name="username"/>
                        <div style={{ fontSize: 12, color: "red" }}>
                            {this.state.usernameError}
                         </div>
                    </div>
                    <div><label htmlFor="password">Password</label>
                    <input type="text" 
                    onChange={this.onChange}
                    value={this.state.password}
                    name="password"/></div>
                    <div>
                        <select 
                        name= "country"
                        value={this.state.country}
                        onChange={this.onChange}>
                            <option value="">Choose your country</option>
                            {options}
                        </select>
                    </div>
                    <div>
                        <input type="checkbox" 
                        name="hasAgreed"
                        value={this.state.hasAgreed}
                        onChange={this.onChange}/>I agree to MYtinerary's Terms and Conditions
                    </div>
                    <button type="submit" className="signUpBtn">OK</button>

                </form>
            </div>
        );
    }
}

export default ValidationForm;
