import React, { Component } from 'react';
import {
    connect
} from 'react-redux';
import * as actionCreator from './Store/Actions/actions';
import { Link } from 'react-router-dom';
class UserIcon extends Component {
    render(){
        const styles ={
            width:'55%',          
        } 
        const { isAuthenticated } = this.props.auth;
        const userLinks = (
            <div onClick={this.props.LogOutUser}>Log out</div>
        );
        const guestLinks = (
                <div className="LoginLinks">
                    <div><Link to="/Login">Log in</Link></div>
                    <div><Link to="/Account">Create Account</Link></div>
                </div>                      
        )

            if (this.props.userLoggedIn === true){
                return(
                    <div style={styles}>
                        <details>
                            <summary><div className="User"><i className="fas fa-user-circle"></i></div></summary>
                            <div className="build">
                            <div onClick={this.props.LogOutUser}>Log out</div>
                            </div>
                        </details>
                    </div>
                )
            }
            else{          
                return(
                <div style={styles}>
                    <details>
                        <summary><div className="User"><i className="fas fa-user-circle"></i></div></summary>
                        <div className="build">
                            <div className="LoginLinks">
                            <div><Link to="/Login">Log in</Link></div>
                            <div><Link to="/Account">Create Account</Link></div>                       
                            </div>
                        </div>

                    </details>                
                </div>
            )
        }
    }
}

// export default UserIcon;

const mapStateToProps = (state) => {

    return {
        userLoggedIn: state.userLoggedIn,
        auth: state.auth
    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        LogInUsers: (user) => {
            dispatch(actionCreator.LogInUsers(user))
        },
        LogOutUser: () => {
            dispatch(actionCreator.UserLoggedOut())
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserIcon);
