let data = [
    { id: 1, name: 'foo' },
    { id: 2, name: 'bar' },
    { id: 3, name: 'baz' },
    { id: 4, name: 'qux' },
    { id: 5, name: 'quux' },
    { id: 6, name: 'foobar' }
  ];
class Filter extends Component{
    constructor(props){
        super(props);
        this.state={
            data: props.data
        };

    }
    handleFilterVal(val){
        const line= this.props.data.filter((item)=>(
            item.id.toString().indexOf(val) >= 0
            ||
            item.name.toLowerCase().indexOf(val) >= 0
        ));
        this.setState({
            data:line
        });
    }
    render(){
        let list = this.state.data.map((data)=>(
            <li key={data.id}>
                {data.id}:{data.name}
            </li>
        ));
    return(
        <div>
            <Form onFilterVal={this.handleFilterVal.bind(this)}/>
            <ul>
                {list}
            </ul>
        </div>
    )

    }
}
Filter.propTypes ={
    data: React.PropTypes.array.isRequired
};
