import React, { Component } from 'react';
import '../Styles/SignUpForm.css'

const countries=["England",
"Germany",
"France",
"Holland",
"Ireland",
"Spain",
"United States"];

class SignUpForm2 extends Component {
    constructor(){
        super();
        this.state ={
            username: "",
            password: "",
            country: "",
            hasAgreed: false,
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChange(e){
        this.setState({[e.target.name]:e.target.value});
    }
    onSubmit(e){
        e.preventDefault();
        console.log(this.state);
    }
    render(){
        console.log(this.state)
        const options = countries.map((option, key)=>{
            return(
            <option value={option} key={option}>{option}</option>
            )
        })
        // const options = this.state.countries.map((option, key)=>{
        //     return(
        //     <option value={option}>{option}</option>
        //     )
        // })
        return(
            <div>
                <form onSubmit={this.onSubmit}>
                    <div><label htmlFor="username">Username</label>
                    <input type="text" 
                    onChange={this.onChange}
                    value={this.state.username}
                    name="username"/></div>
                    <div><label htmlFor="password">Password</label>
                    <input type="text" 
                    onChange={this.onChange}
                    value={this.state.password}
                    name="password"/></div>
                    <div>
                        <select 
                        name= "country"
                        value={this.state.country}
                        onChange={this.onChange}>
                            <option value="">Choose your country</option>
                            {options}
                        </select>
                    </div>
                    <div>
                        <input type="checkbox" 
                        name="hasAgreed"
                        value={this.state.hasAgreed}
                        onChange={this.onChange}/>I agree to MYtinerary's Terms and Conditions
                    </div>
                    <button type="submit" className="signUpBtn">OK</button>

                </form>
            </div>
        );
    }
}

export default SignUpForm2;
