import React, { Component } from 'react';
import Hamburger from './Nav';
import { connect } from 'react-redux';
import * as actionCreator from './Store/Actions/actions';

let cityUrl = window.location.pathname.split("/")[2];

class MYtinerary extends Component {
    constructor(){
        super()
        this.state ={
            showItinerary:[],

            counter:0
        }
    }


    render() {
        const styles = {
            border: '1px solid black',
            height:'20vh',
            display:'flex',
            flexDirection: 'column'

        }
        if(this.props.citiesIsLoaded == true){
            this.state.counter++;
            if(this.state.counter == 1){
                this.setState({
                    showItinerary:this.props.itineraries
                })
            }
        }
        return (
            <div>
                <Hamburger />             
                <p>Available MYtineraries</p>

                <div>
                    {/* {this.state.showItinerary} */}
                </div>
                <div style={styles}>
                    <div>
                        <div>user</div>
                        <div>
                            <div>title</div> 
                            <div>Likes</div>
                            <div>Hours</div>
                            <div>$$</div>
                            <div>#hashtag</div>
                        </div>
                    </div>
                    <div>View All</div>
                </div>
            </div>

        );
    }
    componentDidMount(){
        this.props.fetchItineraries(cityUrl);
    }
}

const mapStateToProps =(state)=>{
    return {
        itineraries: state.itineraries,
        citiesIsLoaded: state.citiesIsLoaded
    }
}
const mapDispatchToProps =(dispatch)=>{
    return {
        fetchItineraries: (city) => {
            dispatch(actionCreator.fetchItineraries(city))
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MYtinerary);
